PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "App_job" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "location" varchar(255) NOT NULL, "salary" decimal NOT NULL, "posted_date" datetime NOT NULL, "status" varchar(10) NOT NULL, "company_name" varchar(255) NOT NULL, "job_details" text NOT NULL, "contact_number" varchar(15) NOT NULL, "created_at" datetime NOT NULL, "duration" varchar(100) NOT NULL, "email" varchar(254) NOT NULL, "hourly_rates" varchar(100) NOT NULL, "job_name" varchar(255) NOT NULL, "vacancies" integer NOT NULL, "skills" varchar(255) NOT NULL, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "experience" varchar(50) NOT NULL, "job_code" varchar(20) NOT NULL UNIQUE, "job_time" varchar(50) NOT NULL, "short_description" text NOT NULL, "work_hour" integer unsigned NOT NULL CHECK ("work_hour" >= 0));
COMMIT;
